changeSet.createProcedure:
CREATE OR REPLACE FUNCTION common.update_audit_columns()
RETURNS TRIGGER AS $$
BEGIN
IF
TG_OP ='INSERT' THEN
        NEW.created_at := CURRENT_TIMESTAMP;
        NEW.updated_at := CURRENT_TIMESTAMP;
ELSIF
TG_OP = 'UPDATE' THEN
        NEW.updated_at := CURRENT_TIMESTAMP;
END IF;
RETURN NEW;
END;
$$
LANGUAGE plpgsql;
